import React from 'react';
import productsList from './products.json';

class Products extends React.Component {
  constructor() 
  {
    super()
    this.state = {
      productsList,
      filteredProducts: []
    }
  }

  componentDidMount()
  {
    let productsList = this.state.productsList;
    const filteredProducts = productsList.filter((product) => 
    {
      return product.tag == Products; 
    }) 
    this.setState({
      filteredProducts
    })

  }

  render() {
    return (
      <div>
        <select value={this.state.guess} onChange={this.handleChange}>
          <option value="all">All Products</option>
          <option value="cameras">Cameras</option>
          <option value="lens">Lens</option>
          <option value="tripods">Tripods</option>
          <option value="Lights">Lights</option>
          <option value="camera-bags">Camera Bags</option>
          <option value="storage">Storage</option>
        </select>

      {this.state.filteredProducts.map((item => {
        return(
        <div>
          <p>this is my products page</p>
          <h2>{item.title}</h2>
          <img src={item.image} alt={item.alt}/>
          <p>{item.price}</p>
          <p>{item.description}</p>
        </div>)
        })
      )}
      </div>
    );
  }
  }



export default Products;
