import React from 'react';

class Reviews extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            viewContacts: []
        }    
    }

    componentDidMount(){
        let dataURL = '/api/contacts';
        fetch(dataURL)
        .then(res => res.json())
        .then(details => {
            this.setState({
                viewContacts: details
            });
        })
    };

    render(){
        console.log('state - ', this.state);
        if (this.state.viewContacts.length == 0){
            return <div>loading</div>
        }
        return(
            <div>
                <h1>Reviews</h1>
                {
                    this.state.viewContacts.map(({ first_name, last_name, email, topic, contact_comment }) => (
                        <div>
                            <p>{ first_name }</p>
                            <p>{ last_name }</p>
                            <p>{ email }</p>
                            <p>{ topic }</p>
                            <p>{ contact_comment }</p>
                        </div>
                    ))
                }
            </div>
        )
    }
}

export default Reviews
