import React from 'react';

class Reviews extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            viewContacts: []
        }    
    }

    componentDidMount(){
        let dataURL = '/api/contacts';
        fetch(dataURL)
        .then(res => res.json())
        .then(details => {
            this.setState({
                // viewContacts: details
            });
        })
    };

    render(){
        console.log('state - ', this.state);
        if (this.state.viewContacts == []){
            return <div>loading</div>
        }
        return(
            <div>
                {
                    this.state.viewContacts.map(({
                        first_name
                    }) => (
                        <div>
                            <p>{first_name}</p>
                        </div>
                    ))
                }
            </div>
        )
    }
}

export default Reviews
