import React from 'react';
import './index.css';
import Form from "./form/form";

class Contact extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            viewContacts: false
        }    
        this.handleButton = this.handleButton.bind(this)
        // viewContacts = React.createRef();
    }


    handleButton() {
        // let { viewContacts } = this.state
        this.setState({
            viewContacts: true
        });
    };
    fetchContacts() {
        let dataURL = '/api/products'
        fetch(dataURL)
        .then(res => res.json())
        .then(items => {
        this.setState({
            products: items
        })
      });
    }
    render() 
    {
        return (
            <div className="main">
              <Form />
              <button className="reviews" type="button" onClick={this.handleButton} >View Reviews!</button>
            </div>
        );
    }
}

export default Contact
