import React from 'react';
import "bootstrap/dist/css/bootstrap.css";
import "bootstrap/dist/css/bootstrap-theme.css";
import FormErrors from "./FormErrors";

class Form extends React.Component {
    constructor (props) {
        super(props);
        this.state = {
          fullName: '',
          email: '',
          formErrors: {fullName: '', email: ''},
          emailValid: false,
          passwordValid: false,
          formValid: false
        }
      }

      handleUserInput (e) {
        const name = e.target.name;
        const value = e.target.value;
        this.setState({[name]: value});
      }
///////////////////////
    render() 
    {
        return(
            <div className="form demoForm">
            <form className="" action="#" method="POST" name="contact_us">
              <fieldset>
                <legend id="title">Say Hello!</legend>
                <p>Give us feedback, or just say hello. Send us a message and we'll be glad to see it, or just drop your email!</p>

                <div className="box">
                  <label htmlfor="fullName">Full Name: </label> 
                  <input id="fullName" type="text" name="full name" value={this.state.fullName} onChange={(event) => this.handleUserInput(event)}/> 
                  <div className='panel panel-default'>
                  <FormErrors formErrors={this.state.formErrors} />
                  </div>
                </div>

                <div className="box">
                  <label htmlfor="email">Email: </label>
                  <input id="email" type="email" name="email" value={this.state.email} onChange={(event) => this.handleUserInput(event)}/>
                </div>



                <div className="box comment">
                  <label htmlfor="response">Anything you want to tell us?</label> 
                  <textarea id="response" name="comments" rows="10" cols="30"></textarea>
                </div>

                <div className="submit">
                  <button className="btn btn-light btn-sm waves-effect waves-light" type="submit">Submit</button>
                </div>

              </fieldset> 
            </form>
            </div>
        )
    }
}


export default Form
